mapper.select:
select * from bbs_comment
where bbs_no=#{bbsNo}
and com_no=#{comNo}

mapper.update:
update bbs_comment
set
com_content=#{updateCom.comContent}
where com_no=#{comNo}
and bbs_no=#{bbsNo}

update bbs_comment
set
com_show=1
where com_no=#{comNo}
and bbs_no=#{bbsNo}

update bbs_comment
set
com_show=2
where com_no=#{comNo}
and bbs_no=#{bbsNo}
