mapper.update:
update BBS
set
BBS_title=#{updateItem.bbsTitle},
BBS_content=#{updateItem.bbsContent}
where BBS_no=#{bbsNo}

update BBS
set BBS_show=1
where BBS_no=#{bbsNo}

update BBS
set BBS_show=2
where BBS_no=#{bbsNo}

update BBS
set BBS_show=0
where BBS_no=#{bbsNo}

mapper.select:
select * from reply
where bbs_no=#{bbsNo}
order by re_date asc

mapper.update:
update Reply
set
re_content=#{updateRe.reContent}
where BBS_no=#{bbsNo}

update Reply
set
re_content=#{replyItem.reContent}
where BBS_no=#{bbsNo} 
and re_no=#{reNo}

update Reply
set
re_show=1
where BBS_no=#{bbsNo} 
and re_no=#{reNo}
