mapper.update:
update users
set
user_status = {updateUser.userStatus}
block_reason = {updateUser.blockReason}
where user_no = #{userNo}

mapper.select:
select user_no, user_email,user_nick,join_date, user_grade,user_status,leave_date,block_reason from ( select user_no, user_email,user_nick,join_date, user_grade,user_status,leave_date,block_reason, (row_number() over(order by user_no asc)) as rNum from users
and user_no = #{keyword}
and user_email like '%' || #{keyword} || '%'
and user_nick like '%' || #{keyword} || '%'
) where rNum between #{rowStart} and #{rowEnd} order by user_no asc
